mapper.update:
/* 상품 수정 */
	UPDATE goods
	
		goods_code = #{goodsCode}
		,company_member_code = #{categoryMemberCode}
		,category_code_sub = #{categoryCodeSub}
		,id = #{goodsId}
		,goods_name = #{goodsName}
		,goods_price = #{goodsPrice}
		,goods_image = #{goodsImage}
		,goods_quantity = #{goodsQuantiy}
		,goods_contents = #{goodsContents}
		,registration_datetime = NOW()
	
	WHERE
		goods_code = #{goodsCode};

mapper.select:
		    /* 상품 카테고리 대분류 조회 */
		    SELECT
		    	 category_code as categoryCode
				,category_name as categoryName
			FROM 
				company_category;

		    /* 상품 카테고리 중분류 조회 */
			SELECT
				 category_code_sub as categoryCodeSub
				,category_sub_name as categorySubName
			FROM 
			company_category_sub;
